mapper.select:
SELECT * FROM (
	SELECT a.*, ROWNUM rnum
		FROM (
			SELECT id, status, title, author, view_count, created_at
			FROM t_board_post


					title LIKE '%' || #{keyword} || '%' OR content LIKE '%' || #{keyword} || '%'


			ORDER BY id DESC
		) a
	WHERE ROWNUM <= #{offset} + #{pageSize}
	)
WHERE rnum > #{offset}

mapper.update:
UPDATE t_board_post
SET
	title=#{request.title},
	content=#{request.content},
	author=#{request.author}
WHERE id=#{postId}
